mapper.select:
select *
from im_chat_history

    sender_id = #{senderId}
    or recipient = #{senderId}

        and text like '%${text}%'


        and create_time >= #{queryStart}


        and create_time <= #{queryEnd}

mapper.update:
update im_chat_history
set del_flag = '0'
where sender_id = #{sender_id}
and text = #{text}
